use super::action_result_enum::ActionResult;use crate::constants;use ragit_types::Chunk;impl ActionResult {    pub fn render_read_file_long(chunks: &Vec<Chunk>) -> String {        format!("{} {}", constants::RENDER_FILE_LONG, chunks.iter().enumerate().map(                |(index, chunk)| format!(                    "{} {} {} {} {}", constants::RENDER_FILE_LONG_CHUNK,                    index + 1,                    chunk.render_source(),                    chunk.uid.abbrev(9),                    chunk.summary,                )            ).collect::<Vec<_>>().join("\n\n"))    }}